import time
import matplotlib.pyplot as plt
import pyclipr
import numpy as np
from clipper import clipper2_func


# -------- 主逻辑：外轮廓不断向内缩小 --------
def generate_pockets(outer, holes, step=0.5, scale=1000):
    """
    outer: [(x,y), ...]
    holes: list of [(x,y), ...]
    """
    # --- 转成 Clipper2 输入格式 ---
    outer_paths = [np.array(outer)]
    hole_paths = [np.array(h) for h in holes]

    # 所有 holes 组合为 holes_union（Clipper2 的 union）
    pc = pyclipr.Clipper()
    pc.scaleFactor = scale
    pc.addPaths(hole_paths, pyclipr.Subject)
    holes_union = pc.execute(pyclipr.Union, pyclipr.FillRule.NonZero)
    # x_coords, y_coords = zip(*holes_union[0])
    # plt.plot(x_coords, y_coords, '-o', markersize=1, label='Points Path', color="orange")
    # plt.show()
    # 初始 outer
    current_outer = outer_paths

    results = []  # 每层 pocket

    while True:
        # 1) current_outer - holes_union
        pocket = clipper2_func.clip_difference(current_outer, hole_paths, scale)

        # plt.plot(x_coords, y_coords, '-o', markersize=1, label='Points Path', color="orange")
        #
        # x_coords, y_coords = zip(*pocket[0])
        # plt.plot(x_coords, y_coords, '-o', markersize=1, label='Points Path', color="red")

        # try:
        #     x_coords, y_coords, z = zip(*current_outer[0])
        # except:
        #     x_coords, y_coords = zip(*current_outer[0])
        # plt.plot(x_coords, y_coords, '-o', markersize=1, label='Points Path', color="orange")
        # plt.show()
        if not pocket:
            break

        results.append(pocket)

        # 2) 缩小外轮廓（相当于 shapely.buffer(-step)）
        current_outer = clipper2_func.offset_polygon(current_outer, -step, scale)

        # print(current_outer)
        if not current_outer:
            break
        # x_coords, y_coords = zip(*current_outer[0])
        # plt.plot(x_coords, y_coords, '-o', markersize=1, label='Points Path', color="orange")
        # plt.show()

    return results


def plot_pockets(paths):
    colors = plt.cm.viridis(np.linspace(0, 1, len(paths)))  # 多层渐变色
    plt.figure(figsize=(8, 6))

    for layer_idx, layer in enumerate(paths):
        for polygon in layer:
            # print(polygon)
            polygon = np.array(polygon)
            if len(polygon) == 0:
                continue
            x, y = polygon[:, 0], polygon[:, 1]

            # print("====================")
            plt.fill(x, y, color=colors[layer_idx], alpha=0.3)
            plt.plot(x, y, color='k')  # 边界线
    plt.gca().set_aspect('equal', adjustable='box')
    plt.show()


