# 图像大小
img_size = (500, 500)

# 创建一个空白图像，初始化零点在左上角
img = np.zeros(img_size, dtype=np.uint8)

# 将坐标系平移至图像中心
center = (img_size[0] // 2, img_size[1] // 2)
shifted_points = [(x + center[0], y + center[1]) for x, y in points]

# 将点填充为图像中的区域
cv2.fillPoly(img, [np.array(shifted_points, dtype=np.int32)], color=255)

# 对图像进行二值化
_, thresh = cv2.threshold(img, 0, 255, cv2.THRESH_BINARY)

# 提取轮廓
contours, _ = cv2.findContours(thresh, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE)

# 假设第一个轮廓是外轮廓
external_contour = contours[0]

# 将轮廓点显示出来
points = []
for point in external_contour:
    x, y = point[0]
    points.append([x, y])
    # print(f"Point: ({x}, {y})")
print(points)
